import React from 'react'
import { useState } from 'react'
import Modal from 'react-modal'
import About from './About';
import How from './How';
import { Link } from 'react-router-dom';

export default function Home() {
    const [modalisopen, setmodalisopen] = useState(false);
    const setmodalisopentotrue = () => {
        setmodalisopen(true)
    }
    const setmodalisopentofalse = () => {
        setmodalisopen(false)
    }
    const [modalisopen1, setmodalisopen1] = useState(false);
    const setmodalisopentotrue1 = () => {
        setmodalisopen1(true)
    }
    const setmodalisopentofalse1 = () => {
        setmodalisopen1(false)
    }
    return (
        <>
            <div>
                <h1 className='heading' style={{ display: 'inline-block' }}><center><span style={{ color: 'red' }}>K</span><span style={{ color: 'orange' }}>r</span><span style={{ color: 'yellow' }}>i</span><span style={{ color: '	#00FF00' }}>b</span><span style={{ color: '#00bfff' }}>b</span><span style={{ color: 'blue' }}>l</span><span style={{ color: '#FF1493' }}>e</span><span style={{ color: 'purple' }}>.</span><span style={{ color: 'white', fontSize: '5rem' }}>io</span></center></h1>
                <img src='https://i.gifer.com/origin/cb/cbea37e9ec1839a346ce75f7f8627653_w200.gif' style={{ display: 'inline-block', marginLeft: '0px', margin}} />
            </div>
            <img src='https://singhsimrat.files.wordpress.com/2018/10/yo-bro-sssssss.gif'></img>
            <input className='inputbaby' placeholder='Your Name...' type='text'></input>

            <Link to='/play' style={{ textDecoration: 'none' }}><button className='playbaby'><span style={{ fontSize: '1.1rem', fontFamily: 'fantasy' }}>Play !</span></button></Link>

            <button className='btnbaby' onClick={setmodalisopentotrue}><span style={{ fontSize: '1.1rem', fontFamily: 'fantasy' }}>About</span></button>
            <Modal className='modalbaby' isOpen={modalisopen}>
                <button className='xbaby' onClick={setmodalisopentofalse}>❌</button>
                <About />
            </Modal>
            <button className='btnbaby1' onClick={setmodalisopentotrue1}><span style={{ fontSize: '1.1rem', fontFamily: 'fantasy' }}>How To Play</span></button>
            <Modal className='modalbaby1' isOpen={modalisopen1}>
                <button className='xbaby1' onClick={setmodalisopentofalse1}>❌</button>
                <How />
            </Modal>
        </>
    )
}
