import React, { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import { useCallback } from 'react';
const colors = [
    "red",
    "green",
    "blue",
    "pink",
    "yellow",
    "cyan",
    "brown",
    "black",
    "white",
    "purple",
    "grey",
    "skyblue",
    "orange"
]
export default function Play() {
    const canvasRef = useRef(null);
    const ctx = useRef(null);
    const [selectedColor, setSelectedColor] = useState(colors[0]);
    const [mouseDown, setMouseDown] = useState(false);
    const [lastPosition, setPosition] = useState({
        x: 0,
        y: 0
    });
    useEffect(() => {
        if (canvasRef.current) {
            ctx.current = canvasRef.current.getContext('2d');
        }
    }, []);
    const draw = useCallback((x, y) => {
        if (mouseDown) {
            ctx.current.beginPath();
            ctx.current.strokStyle = selectedColor;
            ctx.current.lineWidth = 10;
            ctx.current.lineJoin = 'round';
            ctx.current.moveTo(lastPosition.x, lastPosition.y);
            ctx.current.lineTo(x, y);
            ctx.current.closePath();
            ctx.current.stroke();

            setPosition({
                x,
                y
            })
        }
    }, [lastPosition, mouseDown, selectedColor, setPosition])

    const onMouseDown = (e) => {
        setPosition({
            x: e.pageX,
            y: e.pageY
        })
        setMouseDown(true)
    }
    const onMouseUp = (e) => {
        setMouseDown(false)
    }
    const onMouseMove = (e) => {
        draw(e.pageX, e.pageY)
    }
    console.log(mouselastPosition, )
    return (
        <>
            <div>
                <div className='deeev1'>
                    <h3 className='heading1'><span style={{ color: 'red' }}>K</span><span style={{ color: 'orange' }}>r</span><span style={{ color: 'yellow' }}>i</span><span style={{ color: '	#00FF00' }}>b</span><span style={{ color: '#00bfff' }}>b</span><span style={{ color: 'blue' }}>l</span><span style={{ color: '#FF1493' }}>e</span><span style={{ color: 'purple' }}>.</span><span style={{ color: 'white', fontSize: '4rem' }}>io</span></h3>
                    <img src='https://clipart.world/wp-content/uploads/2020/08/cartoon-pencil-png-transparent.png' style={{ marginLeft: '0px', marginTop: '0px', marginBottom: '0px', marginRight: '70rem', height: '7rem' }} />
                </div>
                <center><nav class="navbar bg-light" style={{ display: 'block', width: '90rem', height: '4rem', marginTop: '1rem', alignContent: 'normal' }}>
                    <span class="navbar-text">
                        <h3 style={{ textAlign: 'left', display: 'flex' }}><div style={{ marginTop: '7px' }}>ROUND</div><div style={{ marginLeft: '30px' }} > TIME <img className="image2baby" src='https://upload.wikimedia.org/wikipedia/commons/7/7a/Alarm_Clock_GIF_Animation_High_Res.gif' /></div><div style={{ marginLeft: '50rem', marginTop: '7px' }}>Number of Letters:</div></h3>

                    </span>
                </nav>
                </center>
                <nav class="navbar bg-light" style={{ display: 'inline-block', width: '16rem', height: '25rem', marginTop: '0.6rem', marginLeft: '2.5rem', marginBottom: '20rem' }}>
                    <span class="navbar-text">

                    </span>
                </nav>
                {/* <nav class="navbar bg-light" style={{ display: 'inline-block', width: '55rem', height: '45rem', marginTop: '0.6rem', marginLeft: '0.6rem' }}>
                    <span class="navbar-text">

                    </span>
                </nav> */}
                <canvas style={{ display: 'inline-block', width: '55rem', height: '45rem', marginTop: '0.6rem', marginLeft: '0.6rem', border: '1px solid #000', boxShadow: '-2px 1px 9px 6px black' }} ref={canvasRef}
                    onMouseDown={onMouseDown}
                    onMouseUp={onMouseUp}
                    onMouseLeave={onMouseUp} 
                    onMouseMove={onMouseMove} />

                <img className='mg' src='https://www.newsshuttle.com/wp-content/uploads/2020/06/HS2.jpg' />
                <nav calssName='overlay' class="navbar bg-light" style={{ display: 'inline-block', width: '17.8rem', height: '30rem', marginTop: '0.6rem', marginLeft: '0.6rem' }}>
                    <span class="navbar-text">

                    </span>
                </nav>
                <center>
                    <button className='playb' style={{ fontSize: '1.5rem', fontFamily: 'fantasy', marginLeft: '10px', marginRight: '10px', marginTop: '10px' }}>Clear</button>
                    <button className='playb' style={{ fontSize: '1.5rem', fontFamily: 'fantasy' }}>Download</button>
                    <select className='playb' style={{ fontSize: '1.5rem', fontFamily: 'fantasy', width: '10rem', height: '2.4rem', marginLeft: '10px' }} value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
                        {
                            colors.map(
                                color => <option value={color}>{color}</option>
                            )
                        }
                    </select>
                </center>
            </div>
            <input className='inpbaby' type='text' placeholder='Guess here...' />
        </>
    )
}
