import React from 'react';
import Can from '../Can';
import { connect } from "react-redux";
import Getword from './Getword';

function Play(props) {
    const[textValue, setTextValue] = useState('');

    const changeTextValue = () => {
        const len = words.length;
        setTextValue(words)
    }
    const abc = props.user

    return (
        <>
            <div>
                <div className='deeev1'>
                    <h3 className='heading1'><span style={{ color: 'red' }}>K</span><span style={{ color: 'orange' }}>r</span><span style={{ color: 'yellow' }}>i</span><span style={{ color: '	#00FF00' }}>b</span><span style={{ color: '#00bfff' }}>b</span><span style={{ color: 'blue' }}>l</span><span style={{ color: '#FF1493' }}>e</span><span style={{ color: 'purple' }}>.</span><span style={{ color: 'white', fontSize: '4rem' }}>io</span></h3>
                    <img src='https://clipart.world/wp-content/uploads/2020/08/cartoon-pencil-png-transparent.png' alt="" style={{ marginLeft: '0px', marginTop: '0px', marginBottom: '0px', marginRight: '70rem', height: '7rem' }} />
                </div>
                <center>
                    <nav class="navbar bg-light" style={{ display: 'block', width: '90rem', height: '4rem', marginTop: '1rem', alignContent: 'normal' }}>
                        <span class="navbar-text">
                            <h3 style={{ textAlign: 'left', display: 'flex' }}><div style={{ marginTop: '7px' }}>
                                {/* <button>Get a Word</button> */}
                                <Getword />
                            </div>
                                <div style={{ marginLeft: '30px' }} ><img className="image2baby" src='https://upload.wikimedia.org/wikipedia/commons/7/7a/Alarm_Clock_GIF_Animation_High_Res.gif' alt="" /></div><div style={{ marginLeft: '50rem', marginTop: '7px' }}>Number of Letters:</div></h3>
                        </span>
                        <Can />
                    </nav>
                </center>
                <nav class="navbar bg-light" style={{ position: 'absolute', display: 'inline-block', width: '16rem', height: '25rem', marginTop: '0.6rem', marginLeft: '2.5rem', marginBottom: '40rem' }}>
                    <span class="navbar-text">
                        <h1 style={{ marginLeft: '30px' }}>{abc}</h1>
                    </span>
                </nav>
                {/* <img className='mg' style={{ marginLeft: '58rem' }} src='https://www.newsshuttle.com/wp-content/uploads/2020/06/HS2.jpg' alt="" /> */}
                <nav calssName='overlay' class="navbar bg-light" style={{ position: 'absolute', display: 'inline-block', width: '17.8rem', height: '30rem', marginTop: '3rem', marginLeft: '76.8rem' }}>
                    <span class="navbar-text">
                    </span>
                </nav>
            </div>
            <input className='inpbaby' type='text' placeholder='Guess here...' />
        </>
    )
}

const mapStateToProps = (state) => {
    return {
        user: state.bats
    }
}

export default connect(mapStateToProps)(Play)
